import java.util.Date;

/**
* Prueba básica de un set de Sudokus
* 
* @author Miguel Cantón Cortés
*/

public class Test 
{
	public static final String SET_SUDOKUS = "";
	public static final int TAM_SET = 80;
		
	public static void main (String args[])
	{
		SudokuSolver sudoku;
		long   inicio, fin, tiempo = 0;
		
		for(int i = 0; i < TAM_SET; i++) {
			// Resolución del sudoku
			inicio = (new Date()).getTime();
	
			sudoku = new SudokuSolver(SET_SUDOKUS.substring(i*81, i*81+81));
			sudoku.solve();
			
			fin = (new Date()).getTime();
			tiempo += fin - inicio;
			

			// Resultado
			//System.out.println("Solución:");
			//System.out.println(sudoku.toString());
			//System.out.println("Tiempo: "+tiempo+" milisegundos.");
		}
		System.out.println("Tiempo: "+tiempo+" milisegundos.");
	}
}
 
                
 
